mapper.select:
SELECT


,gongsi.gongsi_name as gongsiName
,gongsi.gongsi_types as gongsiTypes
,gongsi.gongsi_phone as gongsiPhone
,gongsi.gongsi_email as gongsiEmail
,gongsi.gongsi_photo as gongsiPhoto
,gongsi.gongsi_content as gongsiContent
,gongsi.gongsi_delete as gongsiDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_email as yonghuEmail
,yonghu.yuanxi_types as yuanxiTypes
,yonghu.zhuanye_types as zhuanyeTypes
,yonghu.nianji_types as nianjiTypes
,yonghu.yonghu_delete as yonghuDelete

FROM xinzi  a
left JOIN gongsi gongsi ON a.gongsi_id = gongsi.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.gongsi_id = #{params.gongsiId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.xinzi_uuid_number like CONCAT('%',#{params.xinziUuidNumber},'%')


        and a.xinzi_month like CONCAT('%',#{params.xinziMonth},'%')


          and a.jiben_jine >= #{params.jibenJineStart}   


          and a.jiben_jine <= #{params.jibenJineEnd}   


        and a.jiben_jine = #{params.jibenJine}


          and a.jiangjin_jine >= #{params.jiangjinJineStart}   


          and a.jiangjin_jine <= #{params.jiangjinJineEnd}   


        and a.jiangjin_jine = #{params.jiangjinJine}


          and a.jixiao_jine >= #{params.jixiaoJineStart}   


          and a.jixiao_jine <= #{params.jixiaoJineEnd}   


        and a.jixiao_jine = #{params.jixiaoJine}


          and a.butie_jine >= #{params.butieJineStart}   


          and a.butie_jine <= #{params.butieJineEnd}   


        and a.butie_jine = #{params.butieJine}


          and a.shifa_jine >= #{params.shifaJineStart}   


          and a.shifa_jine <= #{params.shifaJineEnd}   


        and a.shifa_jine = #{params.shifaJine}


        and a.xinzi_content like CONCAT('%',#{params.xinziContent},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.gongsi_id IS NOT NULL


        and gongsi.gongsi_name like CONCAT('%',#{params.gongsiName},'%')


        and gongsi.gongsi_types = #{params.gongsiTypes}



        and gongsi.gongsi_phone like CONCAT('%',#{params.gongsiPhone},'%')


        and gongsi.gongsi_email like CONCAT('%',#{params.gongsiEmail},'%')


        and gongsi.gongsi_content like CONCAT('%',#{params.gongsiContent},'%')


          and gongsi.gongsi_delete >= #{params.gongsiDeleteStart}   


          and gongsi.gongsi_delete <= #{params.gongsiDeleteEnd}   


        and gongsi.gongsi_delete = #{params.gongsiDelete}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


        and yonghu.yuanxi_types = #{params.yuanxiTypes}



        and yonghu.zhuanye_types = #{params.zhuanyeTypes}



        and yonghu.nianji_types = #{params.nianjiTypes}



          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


order by a.${params.sort} ${params.order}
